import React, {Component} from 'react';
import {Card, CardImg, CardText, CardBody, CardTitle, Breadcrumb, BreadcrumbItem, Media, Button, Modal, ModalHeader, ModalBody, Row, Col, Label, Form , FormGroup, Input, Nav, NavItem }from 'reactstrap';
import {Link} from 'react-router-dom';
import {Control, LocalForm, Errors} from 'react-redux-form';


class CommentForm extends Component{
    constructor(props) {
        super(props);


        this.state={
            isModalOpen : false
        }
        this.toggleModal=this.toggleModal.bind(this);
        this.handleSubmit=this.handleSubmit.bind(this);
    }

    toggleModal(){
        this.setState=({
            isModalOpen : ! this.state.isModalOpen
        });
    }

handleSubmit(values){
    this.toggleModal();
    alert('Current state is : ' + JSON.stringify(values));
}

render()   {
    return(
<div>
  <Nav className="ml-auto" navbar>
      <NavItem>
      <Button outline onClick={this.toggleModal}>
                        <span className="fa fa-pencil"></span> Submit Comment </Button>        
      </NavItem>
      </Nav>
      <Modal isOpen={this.state.isModalOpen} toggle={this.toggleModal} >
          <ModalHeader TOGGLE={this.toggleModal}>Submit Comment</ModalHeader>
          <ModalBody>
              <LocalForm onSubmit={(values)=> this.handleComment(values)}>
                  <Row className="form-group">
                      <Label htmlFor="rating" md={12}>Rating</Label>
                      <Col md={12}>
                          <Control.select model=".rating" name="rating" className="form-control">
                              <option1></option1>
                              <option1>2</option1>
                              <option1>3</option1>
                              <option1>4</option1>
                              <option1>5</option1>
                          </Control.select>
                      </Col>
                  </Row>
                  <Row className="form-group">
                      <Label htmlFor=".author" md={12}>Your Name</Label>
                      <Col md={12}>
                          <Control.text model=".author" id="author" name="author" className="form-control"
                          validators={{minLength:minLength(3), maxLength:maxLength(15)}} />
                      </Col>                    
                  </Row>
              </LocalForm>
          </ModalBody>
      </Modal>
      </div>
    );

}

}


    function RenderDish({dish}) {
        return(
            <div className="col-12 col-md-5 m-1 ">
                <Card>
                <CardImg width="100%" src={dish.image} alt={dish.name} />
            <CardBody>
                <CardTitle>{dish.name}</CardTitle>   
                <CardText>{dish.description}</CardText>
            </CardBody>  
            </Card>
            </div>
        );
    }


    function RenderComments({comments}) {
        if(comments != null)
        return(
        <div className="col-12 col-md-5 m-1">
            <h4>Comments</h4>
            <ul className="list-unstyled">
              {comments.map((comment)=>{
                   return(
                     <li key={comment.id}>
                     <p>{comment.comment}</p>
                      <div>-- {comment.author}, {new Intl.DateTimeFormat('en-US', { year: 'numeric', month: 'short', day: '2-digit'}).format(new Date(Date.parse(comment.date)))}</div>
                     </li>
                        );
                    })}
                        </ul>
                        </div>

        );

    else 
     return (
                       <div></div>
                   );
              }



    const DishDetail = (props)=> {
        if(props.dish != null) {
            return (
                <div className="container">
                <div className="row">
                <Breadcrumb>
                    <BreadcrumbItem><Link to="/menu">Menu</Link></BreadcrumbItem>
                    <BreadcrumbItem active>{props.dish.name}</BreadcrumbItem>
                    </Breadcrumb>

                    <div className="col-12">
                        <h3>{props.dish.name}</h3>
                        <hr />
                    </div>
                    </div>
                    <div className="row">
                <RenderDish dish={props.dish} />
                <RenderComments comments={props.comments} />
                </div>
                </div>
            );
        }
         else 
            return (
                <div></div>
            );   




        }
export default DishDetail;
