import React, {Component} from 'react';
import {Card, CardImg, CardImgOverlay, CardText, CardBody, CardTitle, ListGroup, ListGroupItem} from 'reactstrap';

class DishDetail extends Component {

    constructor(props) {
        super(props);        
    }

    renderDish(dish) {
        return(
            <CardBody>
                <CardTitle tag="h6">{this.props.selectedDish.name}</CardTitle>   
                <CardText>{this.props.selectedDish.description}</CardText>
            </CardBody>  
        );
    }

    renderComments(dish) {

        const comments = this.props.selectedDish.comments.map((comment) => {
            const commentDate = new Date(comment.date);
            const month = commentDate.toLocaleString('default', { month: 'short' });
            return(
                <ListGroupItem key={comment.id} className="border-0 m-0 pl-0">
                    <div>{comment.comment}</div>
                    <div>-- {comment.author}, {month} {commentDate.getDate()}, {commentDate.getFullYear()} </div>
                </ListGroupItem>
            );
        });

        return(
            <div>
                <h4>Comments</h4>
                <ListGroup>
                    {comments}            
                </ListGroup>                
            </div>
        );

    }

    render() {
        if(this.props.selectedDish != null) {
            return (
                <div className="row">
                    <div className="col-12 col-md-5 m-1">
                        <Card>
                            <CardImg top src={this.props.selectedDish.image} alt={this.props.selectedDish.name}/>
                            {this.renderDish(this.props.selectedDish)}      
                        </Card>
                    </div>
                    <div className="col-12 col-md-5 m-1">
                        {this.renderComments(this.props.selectedDish)}
                    </div>
                </div>
            );
        } else {
            return (
                <div></div>
            );
        }
    }

}

export default DishDetail;
