import React, { Component } from 'react';
import { Card, CardImg, CardImgOverlay, CardText, CardBody, CardTitle } from 'reactstrap';

class DishDetail extends Component {

    constructor (props) {
        super(props);

        this.state = {
        }
        console.log('Dishdetail Component constructor is invoked');
    }

    componentDidMount () {
        console.log('Dishdetail Component componentDidMount is invoked');
    }

    renderDish(dish) {
        console.log('renderDish called');
        return (
            <Card>
                <CardImg src={dish.image} alt={dish.name} />
                <CardBody>
                    <CardTitle>{dish.name}</CardTitle>
                    <CardText>{dish.description}</CardText>                       
                </CardBody>
            </Card>
        );
    }

    renderComments(comments) {
        console.log('DishDeatil renderComments invoked');
        if (comments != null) {
            const comList = comments.map((comment) => {
                return (
                    <div key={comment.id}>
                        <ul className="list-unstyled">
                            <li>{comment.comment}</li>
                            <li>-- {comment.author} , {comment.date}</li>
                        </ul>
                    </div>
                )
            });
            return comList
        }
        else {
            return (
                <div></div>
            );
        }
    }

    render() {
        if (this.props.selectedDish != null) {
            console.log('Dishdetail Component render is invoked, with DISH');

            return (
                <div className="row">
                    <div className="col-12 col-md-5 m-1">
                        {this.renderDish(this.props.selectedDish)}
                    </div>
                    <div className="col-12 col-md-5 m-1">
                        <h4><strong>Comments</strong></h4>
                        {this.renderComments(this.props.selectedDish.comments)}
                    </div>  
                </div>
            );
        }
        else {
            console.log('Dishdetail Component render is invoked, without DISH');
            return (
                <div></div>
            );
        }
    }
}

export default DishDetail;
